import { useMutation } from "@apollo/client";
import gql from "graphql-tag";
import { Router } from "next/dist/client/router";
import useForm from "../lib/useForm";
import { ALL_USERS_QUERY } from "./Persons";

const CREATE_USER_MUTATION = gql`
  mutation CREATE_USER_MUTATION(
    $name: String!
    $email: String!
    $phoneNumber: Int!
  ) {
    createUser(
      data: { name: $name, email: $email, phoneNumber: $phoneNumber }
    ) {
      id
      name
      email
      phoneNumber
    }
  }
`;

export default function CreatePerson() {
  const { inputs, handleChange, clearForm, resetForm } = useForm({});

  const [createUser, { loading, error, data }] = useMutation(
    CREATE_USER_MUTATION,
    {
      variables: inputs,
      refetchQueries: [{ query: ALL_USERS_QUERY }],
    }
  );
  return (
    <form
      onSubmit={async (e) => {
        e.preventDefault();
        const res = await createUser();
        clearForm();
        Router.push({ pathname: `/person/${res.data.createUser.id}` });
      }}
    >
      <fieldset>
        <label htmlFor="name">
          Name
          <input required type="text" id="name" placeholder="Name" />
        </label>
        <label htmlFor="email">
          Email <input required type="email" placeholder="Email address" />
        </label>
        <label htmlFor="number">
          Phone Number <input type="number" placeholder="phone number" />
        </label>
        <button type="submit">Add User</button>
      </fieldset>
    </form>
  );
}
