import { useMutation } from "@apollo/client";
import Router from "next/router";
import useForm from "../lib/useForm";
import { SIGNUP_MUTATION } from "./SignUp";

// const CREATE_USER_MUTATION = gql`
//   mutation CREATE_USER_MUTATION(
//     $name: String!
//     $email: String!
//     $password: String!
//     $phoneNumber: Int!
