import styled from "styled-components";

const Table = styled.table`
  border-spacing: 0;
  width: 100%;
  border: 1px solid var(--offWhite);
  thead {
    font-size: 10px;
  }
  td,
  th {
    border: 1px solid #ddd;
    padding:8px;
  }
  tr:nth-child(even){
    background-color:#f2f2f2;
  }
  tr {
    &:hover {
      background-color: #ddd;
    }
  }
  th{
    padd
  }
`;

export default Table;
