import { useQuery } from "@apollo/client";
import gql from "graphql-tag";
import { perPage } from "../config";
import ErrorMessage from "./ErrorMessage";
import Table from "./styles/Table";
import User from "./User";

export const ALL_USERS_QUERY = gql`
  query ALL_USERS_QUERY($skip: Int = 0, $first: Int) {
    allUsers(first: $first, skip: $skip) {
      id
      name
      email
      phoneNumber
    }
  }
`;
export default function Users({ page }) {
  const { data, error, loading } = useQuery(ALL_USERS_QUERY, {
    variables: {
      skip: page * perPage - perPage,
      first: perPage,
    },
  });
  if (loading) return <p>Loading...</p>;
  if (error) return <ErrorMessage>Error : {error.message}</ErrorMessage>;
  console.log(data);
  return (
    <div>
      <Table>
        <tr>
          <th>Name</th>
          <th>Email</th>
          <th>Id</th>
          <th>Phone Number</th>
          <th>Actions</th>
        </tr>
        {data?.allUsers.map((user) => (
          <User key={user.id} user={user} />
        ))}
      </Table>
    </div>
  );
}
