import { useLazyQuery } from "@apollo/client";
import gql from "graphql-tag";
import { useRouter } from "next/dist/client/router";
import { DropDown, DropDownItem, SearchStyles } from "./styles/DropDown";

const SEARCH_USERS_QUERY = gql`
  query SEARCH_USERS_QUERY($searchTerm: String!) {
    searchTerms: allUsers(where: { OR: [{ name_contains_i: $searchTerm }] }) {
      id
      name
    }
  }
`;

export default function Search() {
  const router = useRouter();

  const [findUsers, { loading, data, error }] = useLazyQuery(
    SEARCH_USERS_QUERY,
    {
      fetchPolicy: "no-cache",
    }
  );

  const users = data?.searchTerms || [];

  const findUsersButChill = debounce(findUsers, 350);

  resetIdCounter();

  const {
    isOpen,
    inputValue,
    getMenuProps,
    getComboboxProps,
    getUserProps,
    highlightedIndex,
  } = useCombobox({
    users,
    onInputValueChange() {
      findUsersButChill({
        variables: {
          searchTerm: inputValue,
        },
      });
    },
    onSelectedUserChange({ selectedUser }) {
      router.push({
        pathname: `/user/${selectedUser.id}`,
      });
    },
    userToString: (user) => user?.name || ``,
  });
  return (
    <SearchStyles>
      <div {...getComboboxProps()}>
        <input
          {...getInputProps({
            type: "search",
            placeholder: "Search for an user",
            id: "search",
            className: loading ? "loading" : "",
          })}
        />
      </div>
      <DropDown {...getMenuProps()}>
        {isOpen &&
          users.map((user, index) => (
            <DropDownItem
              {...getUserProps({ user })}
              key={user.id}
              highlighted={index === highlightedIndex}
            >
              {user.name}
            </DropDownItem>
          ))}
        {isOpen && !users.length && !loading && (
          <DropDownItem>Sorry , No users found for {inputValue}</DropDownItem>
        )}
      </DropDown>
    </SearchStyles>
  );
}
